INSERT INTO notification_templates (name, subject, content, type) VALUES ('WelcomeTemplate', 'Bienvenido a Nuestro Servicio', '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Welcome to Coworking</title><style>body {font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif; margin: 0; padding: 0; background-color: #f5f7fa; color: #444; display: flex; flex-direction: column; min-height: 100vh;} header {background-color: #a0522d; color: white; padding: 60px 20px; text-align: center;align-items: center;justify-content: center;flex-direction: column; background-image: url("https://images.unsplash.com/photo-1505489304219-85ce17010209?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"); background-size: cover; background-position: center; height: 320px; display: flex; flex-direction: column; justify-content: center; align-items: center; text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5); border-bottom: 4px solid #8b4513;} .container {width: 90%; max-width: 800px; margin: 20px auto; padding: 20px; background-color: white; flex: 1;} h1 {font-size: 2.5em; margin: 0; color: #333;} h2 {color: #333; margin-top: 15px; font-size: 1.5em;} p {font-size: 1.1em; line-height: 1.6; margin: 10px 0;} footer {text-align: center; padding: 15px 0; background-color: #333; color: white;} .btn {display: inline-block; background-color: #8b4513; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; margin: 10px 0; transition: background-color 0.3s;} .btn:hover {background-color: #7a3e12;}</style></head><body><header><h1>Welcome to Unique Coworking!</h1><p>Weâ€™re thrilled to have you join us!</p></header><div class="container" id="content"><h2>Hello, <span id="userName">[User Name]</span>!</h2><p>Welcome to our community, where creativity and collaboration bring ideas and projects to life.</p><h2>Your Account Information</h2><p><strong>Name:</strong> <span id="userNameInfo">[User Name]</span></p><p><strong>Email:</strong> <span id="userEmail">[User Email]</span></p><p><strong>Registration Date:</strong> <span id="userRegistrationDate">[Registration Date]</span></p><h2>Explore Our Spaces</h2><p>Our workspace is designed to boost your creativity and productivity. Discover modern areas, fully equipped meeting rooms, and relaxing spaces that foster collaboration.</p><h2>What would you like to do next?</h2><a href="#" class="btn">Explore Spaces</a></div><footer><p>&copy; 2024 Coworking. All rights reserved.</p></footer></body></html>', 'email');
