INSERT INTO notification_templates (name, subject, content, type) VALUES ('WelcomeTemplate', 'Bienvenido a Nuestro Servicio', '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Bienvenido a Coworking</title><style>body {font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif; margin: 0; padding: 0; background-color: #f5f7fa; color: #444; display: flex; flex-direction: column; min-height: 100vh;} header {background-color: #a0522d; color: white; padding: 60px 20px; text-align: center;align-items: center;justify-content: center;flex-direction: column; background-image: url("https://images.unsplash.com/photo-1505489304219-85ce17010209?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"); background-size: cover; background-position: center; height: 320px; display: flex; flex-direction: column; justify-content: center; align-items: center; text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5); border-bottom: 4px solid #8b4513;} .container {width: 90%; max-width: 800px; margin: 20px auto; padding: 20px; background-color: white; flex: 1;} h1 {font-size: 2.5em; margin: 0; color: #333;} h2 {color: #333; margin-top: 15px; font-size: 1.5em;} p {font-size: 1.1em; line-height: 1.6; margin: 10px 0;} footer {text-align: center; padding: 15px 0; background-color: #333; color: white;} .btn {display: inline-block; background-color: #8b4513; color: white; padding: 10px 20px; text-decoration: none; border-radius: 4px; margin: 10px 0; transition: background-color 0.3s;} .btn:hover {background-color: #7a3e12;}</style></head><body><header><h1>!Bienvenido a Coworking!</h1><p>¡Estamos emocionados de tenerte con nosotros!</p></header><div class="container" id="content"><h2>Hola, <span id="userName">[User Name]</span>!</h2><p>Bienvenido a nuestra comunidad, donde la creatividad y la colaboración dan vida a ideas y proyectos.</p><h2>Información de tu cuenta</h2><p><strong>Nombre:</strong> <span id="userNameInfo">[User Name]</span></p><p><strong>Email:</strong> <span id="userEmail">[User Email]</span></p><p><strong>Fecha de Registro:</strong> <span id="userRegistrationDate">[Registration Date]</span></p><h2>Explora nuestros espacios</h2><p>Nuestro espacio de trabajo está diseñado para impulsar tu creatividad y productividad. Descubre zonas modernas, salas de reuniones totalmente equipadas y espacios relajantes que fomentan la colaboración.</p><h2>¿Qué te gustaría hacer ahora?</h2><a href="https://workspace-umber-mu.vercel.app/spaces" class="btn">Explorar Espacios</a></div><footer><p>&copy; 2024 Coworking. Todos los derechos reservados.</p></footer></body></html>', 'email');
INSERT INTO notification_templates (name, subject, content, type) VALUES ('ReservationTemplate', 'Factura de Reserva en Coworking', '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Factura de Reserva</title><style>body {font-family: \'Arial\', sans-serif; background-color: #f4f4f9; color: #333; margin: 0; padding: 0; display: flex; justify-content: center; align-items: center; min-height: 100vh;} .invoice-container {background-color: #ffffff; max-width: 600px; border-radius: 8px; box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); overflow: hidden; font-size: 16px;} .brand-header {background-color: #2c3e50; color: white; padding: 20px; text-align: center; font-size: 20px; font-weight: bold;} .brand-header img {max-width: 40px; vertical-align: middle; margin-right: 10px;} .invoice-header {background-color: #4caf50; color: white; padding: 20px; text-align: center;} .invoice-header h1 {margin: 0; font-size: 24px;} .invoice-body {padding: 20px;} .invoice-body p {margin: 8px 0;} .invoice-body p span {font-weight: bold;} .invoice-summary {margin-top: 20px; padding: 15px; background-color: #f9f9f9; border: 1px solid #ddd; border-radius: 8px;} .invoice-summary p {margin: 8px 0; display: flex; justify-content: space-between;} .invoice-summary p span {font-weight: bold;} .total {font-size: 1.5em; color: #4caf50; text-align: right;}</style></head><body><div class="invoice-container"><div class="brand-header"><img src="https://raw.githubusercontent.com/Anthonymss/Workspace_Backend/refs/heads/main/resources/logo.jpg" alt="Logo de Coworking"> Coworking</div><div class="invoice-header"><h1>Factura #[NumeroFactura]</h1></div><div class="invoice-body"><p><span>Fecha de Reserva:</span> [FechaReserva]</p><p><span>Espacio:</span> [Sitio]</p><p><span>Dirección:</span> [Direccion]</p><p><span>Nombre del Espacio:</span> [NombreSpacio]</p><p><span>Duración:</span> [Duracion]</p><p><span>Método de Pago:</span> [MetodoPago]</p></div><div class="invoice-summary"><p><span>Subtotal:</span> <span>S/[Subtotal]</span></p><p><span>Impuesto:</span> <span>S/[Impuesto]</span></p><p class="total"><span>Total:</span> <span>S/[Total]</span></p></div></div></body></html>', 'email');
